const rowsIn = Array.isArray(msg.payload) ? msg.payload : (msg.payload ? [msg.payload] : []);
const rules = global.get('qcRules') || {};
const last  = global.get('qcLast')  || {};

function qcFor(tagName, ts, value) {
  if (value == null || Number.isNaN(Number(value))) return {code: 5, reason: ['invalid']};
  const r = rules[tagName] || {};
  const reasons = [];
  let code = 0;
  if (r.allow_negative === false && value < 0) { code = 6; reasons.push('negative_not_allowed'); }
  if (r.min_val != null && value < r.min_val)   { code = Math.max(code,1); reasons.push('below_min'); }
  if (r.max_val != null && value > r.max_val)   { code = Math.max(code,2); reasons.push('above_max'); }
  const prev = last[tagName];
  if (r.max_step != null && prev && Math.abs(value - prev.value) > r.max_step) { code = Math.max(code,3); reasons.push('spike'); }
  if (r.max_gap_seconds != null) {
    const ageSec = (Date.now() - new Date(ts).getTime())/1000;
    if (ageSec > r.max_gap_seconds) { code = Math.max(code,4); reasons.push('stale'); }
  }
  if (reasons.length === 0) reasons.push('ok');
  return {code, reason: reasons};
}

